mapper.select:
   
       
select no, name, email
       from member
       where email=#{email}
       and password=#{password}

   
       
select no, name, email
       from member
       where no=#{no}

mapper.update:
        
		update member
		   set name = #{name },
		   
        
            	
					password = #{password },
					
        
        	   
			   gender = #{gender } 
		 where no = #{no }
